    def my_fn(config):
        pass

    step = ExecutorStep(name="test", fn=my_fn, config=None, resources=explicit)
    resolved = resolve_executor_step(step, config={}, output_path="/out/test-abc")

    assert resolved.fn.__fray_resources__ == explicit


